function products_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('products');
	if(!$arrPerm[1]){
		return false;
	}
    $data['cost'] = makeSafe($_REQUEST['cost']);
        if($data['cost'] == empty_lookup_value){ $data['cost'] = ''; }
    $data['weight'] = makeSafe($_REQUEST['weight']);
        if($data['weight'] == empty_lookup_value){ $data['weight'] = ''; }
    $data['quantity'] = makeSafe($_REQUEST['quantity']);
        if($data['quantity'] == empty_lookup_value){ $data['quantity'] = ''; }
    $data['category'] = makeSafe($_REQUEST['category']);
        if($data['category'] == empty_lookup_value){ $data['category'] = ''; }
    $data['brand'] = makeSafe($_REQUEST['brand']);
        if($data['brand'] == empty_lookup_value){ $data['brand'] = ''; }
    $data['status'] = makeSafe($_REQUEST['status']);
        if($data['status'] == empty_lookup_value){ $data['status'] = ''; }
    $data['ico'] = PrepareUploadedFile('ico', 1024000,'jpg|jpeg|gif|png', false, '');
	if($data['ico']) createThumbnail($data['ico'], getThumbnailSpecs('products', 'ico', 'tv'));
	if($data['ico']) createThumbnail($data['ico'], getThumbnailSpecs('products', 'ico', 'dv'));

    $data['serial'] = makeSafe($_REQUEST['serial']);
		if($data['serial'] == empty_lookup_value){ $data['serial'] = ''; }
    
    $data['name'] = makeSafe($_REQUEST['name']);
		if($data['name'] == empty_lookup_value){ $data['name'] = ''; }
	if($data['name']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Name': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from products where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['ico']) $data['ico'] = makeSafe($row['ico']);
			
		}
	}
	// hook: products_before_insert
	if(function_exists('products_before_insert')){
		$args=array();
		if(!products_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `products` set       `name`=' . (($data['name'] !== '' && $data['name'] !== NULL) ? "'{$data['name']}'" : 'NULL') . ', `cost`=' . (($data['cost'] !== '' && $data['cost'] !== NULL) ? "'{$data['cost']}'" : 'NULL') . ', `weight`=' . (($data['weight'] !== '' && $data['weight'] !== NULL) ? "'{$data['weight']}'" : 'NULL') . ', `quantity`=' . (($data['quantity'] !== '' && $data['quantity'] !== NULL) ? "'{$data['quantity']}'" : 'NULL'). ', `category`=' . (($data['category'] !== '' && $data['category'] !== NULL) ? "'{$data['category']}'" : 'NULL'). ', `brand`=' . (($data['brand'] !== '' && $data['brand'] !== NULL) ? "'{$data['brand']}'" : 'NULL'). ', `status`=' . (($data['status'] !== '' && $data['status'] !== NULL) ? "'{$data['status']}'" : 'NULL'). ', `ico`=' . (($data['ico'] !== '' && $data['ico'] !== NULL) ? "'{$data['ico']}'" : 'NULL'). ', `serial`=' . (($data['serial'] !== '' && $data['serial'] !== NULL) ? "'{$data['serial']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"products_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: products_after_insert
	if(function_exists('products_after_insert')){
		$res = sql("select * from `products` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!products_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('products', $recID, getLoggedMemberID());

	return $recID;
}

function products_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('products');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='products' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='products' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: products_before_delete
	if(function_exists('products_before_delete')){
		$args=array();
		if(!products_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	
	sql("delete from `products` where `id`='$selected_id'", $eo);

	// hook: products_after_delete
	if(function_exists('products_after_delete')){
		$args=array();
		products_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='products' and pkValue='$selected_id'", $eo);
}

