        if( $dvprint) $templateCode = str_replace('<%%VALUE(cost)%%>', safe_html($urow['cost']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(cost)%%>', html_attr($row['cost']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(cost)%%>', urlencode($urow['cost']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(weight)%%>', safe_html($urow['weight']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(weight)%%>', html_attr($row['weight']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(weight)%%>', urlencode($urow['weight']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(quantity)%%>', safe_html($urow['quantity']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(quantity)%%>', html_attr($row['quantity']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(quantity)%%>', urlencode($urow['quantity']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(category)%%>', safe_html($urow['category']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(category)%%>', html_attr($row['category']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(category)%%>', urlencode($urow['category']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(brand)%%>', safe_html($urow['brand']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(brand)%%>', html_attr($row['brand']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(brand)%%>', urlencode($urow['brand']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', safe_html($urow['status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', html_attr($row['status']), $templateCode);
        $templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode($urow['status']), $templateCode);
		$row['ico']=($row['ico']!=''?$row['ico']:'blank.gif');
		$templateCode=str_replace('<%%VALUE(ico)%%>', html_attr($row['ico']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(ico)%%>', urlencode($urow['ico']), $templateCode);
        if( $dvprint) $templateCode = str_replace('<%%VALUE(serial)%%>', safe_html($urow['serial']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(serial)%%>', html_attr($row['serial']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(serial)%%>', urlencode($urow['serial']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(name)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(name)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(cost)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(cost)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(weight)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(weight)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(quantity)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(quantity)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(category)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(category)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(brand)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(brand)%%>', urlencode(''), $templateCode);
        $templateCode = str_replace('<%%VALUE(status)%%>', '', $templateCode);
        $templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(ico)%%>', 'blank.gif', $templateCode);
        $templateCode = str_replace('<%%VALUE(serial)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(serial)%%>', urlencode(''), $templateCode);
	}
	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('products');
	if($selected_id){
		$jdata = get_joined_record('products', $selected_id);
		if($jdata === false) $jdata = get_defaults('products');
		$rdata = $row;
	}
	$templateCode .= loadView('products-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: products_dv
	if(function_exists('products_dv')){
		$args=array();
		products_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
